<script>
    function highlight_map_states() {
        if ($(".states_section").length > 0) {
            $(".states_section .list_states .item .link").hover(function () {
                var a = "#state_" + $(this).text().toLowerCase();
                $(a).attr("class", "state hover")
            }, function () {
                var a = "#state_" + $(this).text().toLowerCase();
                $(a).attr("class", "state active")
            });
        }
    }

    const queryString = window.location.search;
    if(queryString != ""){
    const urlParams = new URLSearchParams(queryString);

    let type = 'grafico';
    let state_index = $("#unidade-territorial").val();
    const state = urlParams.get('response')
        if(state != ""){
            const city = urlParams.get('cidade')

            if (state == state_index) {
            let a = "#state_" + state.toLowerCase();
            $(a).attr("class", "state active")
            }
            if (state == 'PE') {
                $("#unidade-territorial").val(state);
                if(city != "" && city != null){
                    let cidade = city;
                        console.log('aqui!')
                        $("#cidade").val(cidade);
